  animation: 2s scaleInOut infinite;
}
.loading span:nth-child(1) {
  animation-delay: 0.2s;
}
.loading span:nth-child(2) {
  animation-delay: 0.4s;
}
.loading span:nth-child(3) {
  animation-delay: 0.6s;
