}
#collections .grid {
  display: grid;
  grid-gap: 50px;
}
@media screen and (min-width: 1023px) {
  #collections .grid {
    grid-template-columns: 1fr 1fr 1fr;
  }
}

.grid-item img {
  display: block;
  max-width: 100%;
  width: 100%;
  height: 100%;
  object-fit: cover;
}

@media screen and (min-width: 1023px) {
  .grid-item:nth-child(1) {
    grid-column: 1/3;
    grid-row: 1;
  }

  .grid-item:nth-child(2) {
    grid-column: 3;
    grid-row: 1/3;
  }

  .grid-item:nth-child(3) {
    grid-column: 1;
    grid-row: 2;
  }

  .grid-item:nth-child(4) {
    grid-column: 2;
    grid-row: 2;
  }

  .grid-item:nth-child(5) {
    grid-column: 1;
    grid-row: 3;
  }

  .grid-item:nth-child(6) {
    grid-column: 2/4;
    grid-row: 3;
  }
}
#contact {
  height: 100vh;
  position: relative;
  padding-top: 25px;
}
@media screen and (max-width: 1023px) {
  #contact {
    height: auto;
    padding-bottom: 100px;
  }
}
#contact .contact-overlay {
  background-position: center center;
  background-repeat: no-repeat;
  background-size: cover;
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  opacity: 0.6;
  z-index: -1;
}
#contact .contact-overlay::before {
  content: "";
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  background-image: linear-gradient(rgba(255, 255, 255, 0.3), transparent);
  pointer-events: none;
}
#contact .headline {
  margin-top: 25px;
  margin-bottom: 100px;
}
#contact .form-container {
  max-width: 80%;
  height: auto;
  margin: 0 auto;
}
#contact .form-container .contact-message {
  margin-bottom: 40px;
  max-width: 600px;
  font-size: 1rem;
  font-weight: 100;
  color: rgba(0, 0, 0, 0.6);
}
#contact .form-container .contact-message h3 {
  text-transform: uppercase;
  margin-bottom: 20px;
}
#contact .form-container .contact-message p {
  margin-bottom: 8px;
}
@media screen and (max-width: 1023px) {
  #contact .form-container .contact-message p {
    margin-bottom: 15px;
  }
}
#contact .form-container form {
  display: flex;
  flex-direction: column;
}
#contact .form-container .contact-row {
  display: flex;
  justify-content: space-between;
}
#contact .form-container .input {
  width: 48%;
  margin-bottom: 30px;
}
#contact .form-container textarea,
#contact .form-container input {
  width: 100%;
  padding: 10px;
  border: 1px solid rgba(0, 0, 0, 0.2);
  background: rgba(255, 255, 255, 0.6);
  text-transform: uppercase;
  letter-spacing: 1px;
  border-radius: 0;
  -webkit-appearance: none;
}
#contact .form-container textarea {
  resize: none;
}
#contact .form-container input:-webkit-autofill,
#contact .form-container input:-webkit-autofill:hover,
#contact .form-container input:-webkit-autofill:focus,
#contact .form-container input:-webkit-autofill:active {
  transition: background-color 5000s ease-in-out 0s;
}
#contact .form-container input[type=submit] {
  margin-top: 20px;
  max-width: 300px;
  cursor: pointer;
}

