.square-container {
  display: flex;
  flex-wrap: wrap;
  position: absolute;
  height: 100vh;
  width: 100%;
}

.single-square {
  background: #000;
  width: 25%;
  height: 33.33vh;
  position: relative;
  overflow: hidden;
  opacity: 0;


  div {
    position: absolute;
    width: 100vw;
    height: 100vh;
    top: 0;
    left: 0;
    background-repeat: no-repeat;
    background-position: center center;
    background-size: cover;
  }
}
// @for $i from 1 through 12 {
//   .single-square:nth-child(#{$i}) {
//     opacity: $i * 0.05;
//   }
// }
@for $i from 1 through 12 {
  .single-square:nth-child(#{$i}) {
    div {
      left: (($i + 3) % 4) * -25vw;
      @if $i > 4 {
        top: -33.33vh;
      }
      @if $i > 8 {
        top: -66.66vh;
      }
    }
  }
}

