.featured {
  height: 100vh;
  padding: 75px;
  position: relative;

  @media screen and (max-width: $tablet) {
    height: auto;
    padding: 50px 25px;
  }

  .headline {
    position: absolute;
    top: 75px; right: 18%;

    @media screen and (max-width: $tablet) {
      position: relative;
      top: 0; right: 0;
      margin-bottom: 50px;
    }
  }

  .container {
    display: flex;
    justify-content: space-between;
    height: 100%;
    position: relative;

    @media screen and (max-width: $tablet) {
      flex-direction: column;
    }
  }

  .box {
    &.hide {
      display: none;
    }
  }

  .left,
  .right {
    position: relative;
    overflow: hidden;
    transition: 0.4s;

    img {
      width: 100%;
      height: 100%;
      object-fit: cover;
      overflow: hidden;

      @media screen and (max-width: $tablet) {

      }
    }

    .hover {
      display: flex;
      justify-content: center;
      align-items: center;
      position: absolute;
      color: $white;
      letter-spacing: 2px;
      text-transform: uppercase;
      font-size: 12px;
      top: 0; right: 0; bottom: 0; left: 0;
      background-color: rgba($black, 0.4);
      visibility: hidden;
      opacity: 0;
      transition: 0.3s;

      h2 {
        position: relative;
        cursor: pointer;

        &::after {
          content: "";
          position: absolute;
          right: 0;bottom: -5px; left: 0;
          margin: 0 auto;
          width: 0%;
          border-bottom: 1px solid $white;
          transition: 0.3s;
        }

        &:hover::after {
          // left: 0;
          width: 90%;
        }
      }
    }

    // &:hover .hover {
    //   @media screen and (min-width: $min-tablet) {
    //     visibility: visible;
    //     opacity: 1;
    //   }
    // }

    &.full-width {
      width: 100%;
      height: 100%;

      .close {
        visibility: visible;
        opacity: 1;

        &:hover {
          opacity: 0.7;
        }
      }
    }
  }
  .left {
    position: absolute;
    height: 60vh;
    width: 60%;

    @media screen and (max-width: $tablet) {
      margin-bottom: 25px;
      position: relative;
      width: 100%
    }
  }

  .right {
    position: absolute;
    right: 0;
    bottom: 0;
    height: 60vh;
    width: 60%;

    @media screen and (max-width: $tablet) {
      margin-bottom: 25px;
      position: relative;
      width: 100%
    }
  }

}

sources.scss/sections/_collections.scss:
#collections {
  margin: 0 auto;
  max-width: 1800px;
  width: 100%;
  padding: 25px;
  padding-bottom: 50px;

  .headline {
    margin-bottom: 100px;
  }

  .grid {
    display: grid;
    grid-gap: 50px;

    @media screen and (min-width: $tablet) {
      grid-template-columns: 1fr 1fr 1fr;
    }
  }

}

.grid-item img {
  display: block;
  max-width: 100%;
  width: 100%;
  height: 100%;
  object-fit: cover;
}

@media screen and (min-width: $tablet) {
  .grid-item:nth-child(1) {
    grid-column: 1 / 3;
    grid-row: 1;
  }
  .grid-item:nth-child(2) {
    grid-column: 3 ;
    grid-row: 1 / 3;
  }
  .grid-item:nth-child(3) {
    grid-column: 1 ;
    grid-row: 2 ;
  }
  .grid-item:nth-child(4) {
    grid-column: 2;
    grid-row: 2;
  }
  .grid-item:nth-child(5) {
    grid-column: 1;
    grid-row: 3;
  }
  .grid-item:nth-child(6) {
    grid-column: 2 / 4;
    grid-row: 3;
  }
}

