#contact {
   height: 100vh;
   position: relative;
   padding-top: 25px;

   @media screen and (max-width: $tablet) {
     height: auto;
     padding-bottom: 100px;
   }

   .contact-overlay {
    //  background-image: url('../img/AWSU.png');
     background-position: center center;
     background-repeat: no-repeat;
     background-size: cover;
     position: absolute;
     top: 0; right: 0; bottom: 0; left: 0;
     opacity: 0.6;
     z-index: -1;

     &::before {
      content: "";
      position: absolute;
      top: 0; right: 0; bottom: 0; left: 0;
      background-image: linear-gradient($light-white-opacity, transparent);
      pointer-events: none;
    }
  }

  .headline {
    margin-top: 25px;
    margin-bottom: 100px;
  }

  .form-container {
    max-width: 80%;
    height: auto;
    margin: 0 auto;

    .contact-message {
      margin-bottom: 40px;
      max-width: 600px;
      font-size: 1rem;
      font-weight: 100;
      color: rgba($black, 0.6);

      h3 {
        text-transform: uppercase;
        margin-bottom: 20px;
      }

      p {
        margin-bottom: 8px;

        @media screen and (max-width: $tablet) {
          margin-bottom: 15px;
        }
      }
    }

    form {
      display: flex;
      flex-direction: column;
    }

    .contact-row {
      display: flex;
      justify-content: space-between;
    }

    .input {
      width: 48%;
      margin-bottom: 30px;
    }

    textarea,
    input {
      width: 100%;
      padding: 10px;
      border: 1px solid rgba($black, 0.2);
      background: rgba($white, 0.6);
      text-transform: uppercase;
      letter-spacing: 1px;
      border-radius: 0;
      -webkit-appearance: none;
    }
    textarea {
        resize: none;
    }

    input:-webkit-autofill,
    input:-webkit-autofill:hover,
    input:-webkit-autofill:focus,
    input:-webkit-autofill:active {
        transition: background-color 5000s ease-in-out 0s;
    }

    input[type=submit] {
      margin-top: 20px;
      max-width: 300px;
      cursor: pointer;
    }
  }
}
